import React, { useState } from 'react'
import { Form, Message, Button } from 'semantic-ui-react'
import "./login.css"
import empData from "../../assets/data/employeesData.json"

const Login = () => {
  const [userDetails, setUserDetails] = useState({
    userName: "",
    password: "",
  })
  const [error, setError] = useState({
    userError: false,
    passwordError: false,
  })


  const changeHandler = (e) => {
    const { name, value } = e.target;
    setUserDetails(() => { return { [name]: value } })
  }

  const submitHandler = (e) => {
    e.preventDefault();
    const { userName, password } = userDetails;
    let noUser = false;

    empData.employees.map((e, i) => {
      if (e.userName === userName) {

        setError(() => { return { userError: true } })
        if (e.password === password) {

        } else {
          setError(() => { return { passwordError: true } })
        }
      } else {
        setError(() => { return { userError: true } })
      }
    })


  }


  return (
    <div className='login-container'>
      <div className='login-form-container'>
        <Form className='login-form' onSubmit={ (e) => { submitHandler(e) } }>

          <Form.Input className='form-input'
            label="Username"
            name="userName"
            placeholder="Username"
            value={ userDetails.userName }
            onChange={ changeHandler }
            error={ error.userError }
          />
          { error.userError && (
            <Message
              error
              content="User Does Not Exists"
            />
          ) }
          <Form.Input
            label="Password"
            name="password"
            placeholder="Password"
            value={ userDetails.password }
            onChange={ changeHandler }
            error={ error.passwordError }
          />
          { error.passwordError && (
            <Message
              error
              content="Invalid Password"
            />
          ) }
          <Button type='submit'>Login</Button>
        </Form>
      </div>
    </div >
  )
}

export default Login
