import { faBuildingUser } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import React, { useState } from 'react'
import "./dashboard.css"
import { Card, Icon, Image } from 'semantic-ui-react'
import empData from "../../assets/data/employeesData.json"


const Dashboard = () => {
  const [fullProfile, setFullProfile] = useState(false);
  const [fullProfileIndex, setFullProfileIndex] = useState(0);

  const clickHandler = (index) => {
    setFullProfile(!fullProfile);
    setFullProfileIndex(index);
    console.log(fullProfileIndex)
  }

  return (
    <div className='dashboard-container'>
      <div className='sidebar'>
        <FontAwesomeIcon icon={ faBuildingUser } className='fontawesome'></FontAwesomeIcon>
      </div>
      <div className='main'>
        { empData.employees.map((emp, index) => (
          <Card key={ `emp.userName${index}` } className='profile' onClick={clickHandler}>
            <Image src={ require('../../assets/images/profile-icon.jpg') } style={ { width: '50px', height: '50px' } } ui={ false } />
            <Card.Content className='profile-desc'>
              <Card.Header>{ emp.fullName }</Card.Header>
              <Card.Meta>
                <span className='date'>Joined in { emp.doj }</span>
              </Card.Meta>
              <Card.Description>
                { emp.role }
              </Card.Description>
            </Card.Content>
          </Card>
        )) }
        {fullProfile && (
          <div className='popup'>

          </div>
        )}
      </div>
    </div>
  )
}

export default Dashboard
