import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Button, Form, Message } from "semantic-ui-react";
import empData from "../../assets/data/employeesData.json";
import "./login.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


const Login = () => {
  const [userDetails, setUserDetails] = useState({
    userName: "",
    password: "",
  });
  const [error, setError] = useState({
    userError: false,
    passwordError: false,
  });
  const [userNameEntered, setUserNameEntered] = useState(false);
  const [passwordEntered, setPasswordEntered] = useState(false);
  const [formSubmitted, setFormSubmitted] = useState(false);
  const navigate = useNavigate();

  const changeHandler = (e) => {
    const { name, value } = e.target;
    setUserDetails((prevValues) => {
      return { ...prevValues, [name]: value };
    });
    setError((prevValues) => { return { userError: false, passwordError: false } })
    // setFormSubmitted(!formSubmitted)
  };

  const submitHandler = (e) => {
    // e.preventDefault();
    const { userName, password } = userDetails;
    setFormSubmitted(!formSubmitted)
    if (userName === "") {
      setUserNameEntered(!userNameEntered);
      console.log(userNameEntered, passwordEntered, formSubmitted)
    }

    if (password === "") {
      setPasswordEntered(!passwordEntered);
      return
    }

    if (userNameEntered && passwordEntered) {
      let user = empData.employees.filter((emp, i) => emp.userName === userName);
      console.log(user)

      if (user.length) {
        if (user[0].password === password) {
          console.log("same pass")
          navigate("/dashboard");
        } else {
          console.log("no same pass")
          // console.log(password, user.password)
          setError((prevValues) => { return { ...prevValues, passwordError: true } })
        }
      } else {
        console.log("no user")
        setError((prevValues) => { return { ...prevValues, userError: true } })
      }
    }
  };

  return (
    <div className="login-container">
      <div className="login-form-container">

        <Form
          className="login-form"
          onSubmit={ (e) => {
            submitHandler(e);
          } }
        >

          <Form.Field className="required">
            <Form.Input
              className="form-input required"
              label="Username"
              name="userName"
              placeholder="Username"
              value={ userDetails.userName }
              onChange={ changeHandler }
              error={ error.userError }
            />
          </Form.Field>

          { !userNameEntered && formSubmitted && <Message error content="Please enter your username" /> }
          { error.userError && userNameEntered && <Message error content="User Does Not Exists" /> }
          <Form.Field className="required">
            <Form.Input
              label="Password"
              name="password"
              placeholder="Password"
              value={ userDetails.password }
              onChange={ changeHandler }
              error={ error.passwordError }
            />
          </Form.Field>
          { !passwordEntered && formSubmitted && <Message error content="Please enter your password" /> }
          { error.passwordError && passwordEntered && <Message error content="Invalid Password" /> }
          <Button type="submit">Login</Button>
        </Form>
      </div>
    </div>
  );
};

export default Login;
